INSERT INTO categoria(nome, descricao, icone) VALUES('Introdução a tecnologia', 'O curso de Introdução às Tecnologias é projetado para proporcionar aos participantes uma visão abrangente e prática do mundo tecnológico em constante evolução. Este curso introdutório é perfeito para aqueles que desejam adquirir conhecimentos fundamentais sobre as tecnologias mais relevantes e seu impacto na sociedade atual.', 'https://i0.wp.com/blog.portaleducacao.com.br/wp-content/uploads/2022/02/365-O-que-e%CC%81-tecnologia_.jpg?fit=1200%2C675&amp;ssl=1');
INSERT INTO categoria(nome, descricao, icone) VALUES('Introdução a Redes Sociais', 'O curso de Introdução às Redes Sociais é projetado para fornecer aos participantes uma compreensão abrangente e prática das principais plataformas de redes sociais e como utilizá-las de forma eficaz. Este curso introdutório é perfeito para aqueles que desejam aprender sobre o poder das redes sociais, tanto em um contexto pessoal quanto profissional.', NULL);
INSERT INTO curso(nome, descricao, categoria) VALUES('Introdução ao Computador e Navegação na Internet', 'Aprenda os conceitos básicos sobre computadores, incluindo componentes e funcionalidades, além de explorar o uso da internet para pesquisa e entretenimento.', 1);
INSERT INTO curso(nome, descricao, categoria) VALUES('Introdução às Redes Sociais', 'Conheça as principais redes sociais, como Facebook, Twitter e Instagram, e aprenda a criar um perfil, fazer postagens, interagir com outras pessoas e manter a privacidade nas redes sociais.', 2);
INSERT INTO curso(nome, descricao, categoria) VALUES('Uso de Aplicativos de Mensagens', 'Aprenda a utilizar aplicativos populares de mensagens, como WhatsApp e Messenger, para enviar mensagens de texto, fazer chamadas de voz e vídeo, compartilhar mídia e participar de grupos.', 2);
INSERT INTO curso(nome, descricao, categoria) VALUES('Uso de Dispositivos Móveis', 'Familiarize-se com smartphones e tablets, aprendendo a configurar e personalizar o dispositivo, instalar aplicativos, enviar mensagens e realizar tarefas básicas.', 1);
