INSERT INTO artigo(id, corpo) VALUES (1, 'Conhecer os principais componentes do computador é fundamental para entender seu funcionamento. Processador, memória RAM, disco rígido e placa-mãe são essenciais. 
INSERT INTO artigo(id, corpo) VALUES (2, 'O sistema operacional é essencial. Gerencia recursos, executa tarefas, controla dispositivos, garante segurança.');
INSERT INTO artigo(id, corpo) VALUES (3, 'A introdução ao armazenamento de dados é crucial para iniciantes. Compreender conceitos fundamentais é essencial.');
INSERT INTO artigo(id, corpo) VALUES (4, 'Prepare-se para personalizar seu ambiente de trabalho com nosso guia completo. Dicas, organização, cores e produtividade em foco!');
INSERT INTO artigo(id, corpo) VALUES (5, 'Desvende os segredos dos navegadores de internet com nosso guia completo. Do básico ao avançado, explore todas as funcionalidades e aproveite ao máximo sua experiência de navegação.');
INSERT INTO artigo(id, corpo) VALUES (6, 'Organize e acesse seus favoritos com nosso guia completo. Gerencie seu histórico de navegação de forma eficiente e tenha seus sites favoritos sempre à mão.');
INSERT INTO artigo(id, corpo) VALUES (7, 'Explore um mundo de possibilidades com as mensagens instantâneas. Descubra como essas ferramentas revolucionaram a comunicação online.');
INSERT INTO artigo(id, corpo) VALUES (8, 'Explore as bases do armazenamento de dados com uma visão geral essencial. Compreenda os conceitos fundamentais dessa área fundamental da tecnologia.');
INSERT INTO artigo(id, corpo) VALUES (9, 'Entenda como o sistema operacional gerencia a execução de programas por meio de processos e threads. Explore esse aspecto crucial da computação moderna.');
INSERT INTO artigo(id, corpo) VALUES (10, 'Descubra os elementos essenciais do seu computador nesta visão geral dos componentes. Conheça os principais componentes que fazem seu computador funcionar.');
